mapper.select:
select * 
  from enquiry 

 order by enquiry_no desc limit #{skip}, #{amount}

mapper.update:
update enquiry 
   set enquiry_hits = (enquiry_hits + 1) 
where enquiry_no  = #{enquiry_no}

update enquiry 
   set enquiry_title = #{enquiry_title}, enquiry_content = #{enquiry_content}
 where enquiry_no = #{enquiry_no};
